import React from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
} from '@mui/material';
import {
  PictureAsPdf,
  TableChart,
  Assessment,
  TrendingUp,
} from '@mui/icons-material';

export default function Reports() {
  const reports = [
    {
      title: 'Reporte de Stock',
      description: 'Estado actual del inventario con niveles de stock',
      icon: <TableChart fontSize="large" />,
      color: '#1976d2',
    },
    {
      title: 'Movimientos del Mes',
      description: 'Resumen de entradas y salidas del mes actual',
      icon: <TrendingUp fontSize="large" />,
      color: '#4caf50',
    },
    {
      title: 'Valorización de Inventario',
      description: 'Valor total del inventario y análisis por categoría',
      icon: <Assessment fontSize="large" />,
      color: '#ff9800',
    },
    {
      title: 'Historial de Componente',
      description: 'Historial detallado de movimientos por componente',
      icon: <PictureAsPdf fontSize="large" />,
      color: '#9c27b0',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Reportes
      </Typography>

      <Grid container spacing={3}>
        {reports.map((report, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                cursor: 'pointer',
                transition: 'transform 0.2s',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: 3,
                },
              }}
            >
              <CardContent sx={{ flexGrow: 1, textAlign: 'center' }}>
                <Box
                  sx={{
                    backgroundColor: report.color,
                    borderRadius: '50%',
                    width: 80,
                    height: 80,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    margin: '0 auto 16px',
                  }}
                >
                  {React.cloneElement(report.icon, { sx: { color: 'white' } })}
                </Box>
                <Typography variant="h6" gutterBottom>
                  {report.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {report.description}
                </Typography>
              </CardContent>
              <Box sx={{ p: 2 }}>
                <Button fullWidth variant="outlined">
                  Generar Reporte
                </Button>
              </Box>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Paper sx={{ mt: 4, p: 3 }}>
        <Typography variant="h6" gutterBottom>
          Reportes Recientes
        </Typography>
        <Typography variant="body2" color="text.secondary">
          No hay reportes generados recientemente
        </Typography>
      </Paper>
    </Box>
  );
}
